
CREATE TABLE `akad_validasi_absen` (
  `TANGGAL_AVA` date NOT NULL,
  `KELAS_AVA` int(12) NOT NULL,
  `STATUS_AVA` tinyint(1) NOT NULL DEFAULT '0',
  `USER_AVA` int(12) NOT NULL,
  `CREATED_AVA` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
ALTER TABLE `akad_validasi_absen`
  ADD KEY `USER_AVA` (`USER_AVA`),
  ADD KEY `KELAS_AVA` (`KELAS_AVA`);
ALTER TABLE `akad_validasi_absen`
  ADD CONSTRAINT `akad_validasi_absen_ibfk_1` FOREIGN KEY (`USER_AVA`) REFERENCES `md_user` (`ID_USER`) ON UPDATE CASCADE;
ALTER TABLE `akad_validasi_absen` ADD UNIQUE( `TANGGAL_AVA`, `KELAS_AVA`);
ALTER TABLE `akad_validasi_absen` ADD FOREIGN KEY (`KELAS_AVA`) REFERENCES `akad_kelas`(`ID_KELAS`) ON DELETE RESTRICT ON UPDATE CASCADE;


